import React, { useState } from "react";

const ResultadoPaises = (query, estadoPagina, datos)=>{
    if (query[0]) return <p className="parratoCargando">Loading ...</p>;
    if (query[1]) return <p className="parratoError">error</p>;

    if (query[2]) {
      return datos
        ?.slice(
            estadoPagina * 10,
            estadoPagina * 10 + 10
        )
        .map((listaBusqueda, index) => (
          <article key={index}>
            <div className="divContenedorDatosArticuloTitulo ">
              <h1>Pais {listaBusqueda.emoji}</h1>
              <h2>{listaBusqueda.name}</h2>
            </div>
            <div className="divContenedorDatosArticulo ">
              <h2>Capital:</h2>
              <h2>{listaBusqueda.capital}</h2>
            </div>
            <div className="divContenedorDatosArticulo ">
              <h2>lenguas:</h2>
              <h2>{listaBusqueda.languages[0]?.name}</h2>
            </div>

            <div className="divContenedorDatosArticulo ">
              <h2>Continente:</h2>
              <h2>{listaBusqueda.continent.name}</h2>
            </div>
          </article>
        ));
    } else {
      return <div>no data</div>;
    }
}

export default ResultadoPaises;
