import React, { useEffect, useState } from "react";
import "./App.css";
import "./constantes.css";
import ApolloClient from "apollo-boost";
import { ApolloProvider, useQuery } from "@apollo/react-hooks";
import graphqlQuery from "./graphql";
import Paginacion from "./componentes/paginacion";
import { useLazyQuery } from "@apollo/react-hooks";

const App = () => {
  const [estadoBotones, setEstadoBotones] = useState({
    SeleccionOrden: 0,
    EstadoPagina: 0,
  });
  const [estadoInput, setEstadoInput] = useState({
    buscar: "",
  });
  const [estadoListaPaises, setEstadoListaPaises] = useState([]);

  const { loading, error, data, refetch } = useQuery(
    graphqlQuery().filtrarPorNombre,
    { onCompleted: setEstadoListaPaises }
  );

  const [getResult, results] = useLazyQuery(graphqlQuery().filtrarPorNombre);

  const cambiarOrden = (e) => {
    setEstadoBotones({
      ...estadoBotones,
      SeleccionOrden: e.currentTarget.name == "Continente" ? 0 : 1,
    });
  };

  const filtrarLista = (array, input) => {
    return array.filter((str) => {
      return str.name.toLowerCase().indexOf(input) !== -1;
    });
  };

  const ResultadoBusqueda = () => {
    if (loading) return <p>Loading ...</p>;
    if (error) return <p>error</p>;

    const OrdenarPaisesPor = (dataApollo) => {
      return dataApollo?.countries.sort((a, b) => {
        let nombre1 = null;
        let nombre2 = null;
        switch (estadoBotones.SeleccionOrden) {
          case 0:
            nombre1 = a.continent.name.toLowerCase();
            nombre2 = b.continent.name.toLowerCase();

            break;

          case 1:
            nombre1 = a.languages[0]?.name.toLowerCase();
            nombre2 = b.languages[0]?.name.toLowerCase();

            break;

          default:
            break;
        }

        if (nombre1 < nombre2) {
          return -1;
        }
        if (nombre1 > nombre2) {
          return 1;
        }
        return 0;
      });
    };


    return filtrarLista(OrdenarPaisesPor([estadoListaPaises]), estadoInput.buscar)
      .slice(estadoBotones.EstadoPagina, estadoBotones.EstadoPagina + 10)
      .map((asd, index) => {
        return (
          <article className="flexBox RowWarp" key={index}>
            <h1>Pais</h1>
            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>Nombre:</h2>
              <h2>{asd.name}</h2>
            </div>
            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>Capital:</h2>
              <h2>{asd.capital}</h2>
            </div>
            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>lenguas:</h2>
              <h2>{asd.languages[0]?.name}</h2>
            </div>

            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>Continente:</h2>
              <h2>{asd.continent.name}</h2>
            </div>
          </article>
        );
      });

    /*  listaPaises.slice(0, 10).map((asd, index) => {
      if (
        asd.name.toLowerCase().indexOf(estadoInput.buscar.toLowerCase()) !== -1
      ) {
        return (
          <article className="flexBox RowWarp" key={index}>
            <h1>Pais</h1>
            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>Nombre:</h2>
              <h2>{asd.name}</h2>
            </div>
            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>Capital:</h2>
              <h2>{asd.capital}</h2>
            </div>
            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>lenguas:</h2>
              <h2>{asd.languages[0]?.name}</h2>
            </div>

            <div className="divContenedorDatosArticulo flexBoxRow">
              <h2>Continente:</h2>
              <h2>{asd.continent.name}</h2>
            </div>
          </article>
        );
      }
    }); */
  };

  useEffect(() => {}, [estadoInput]);
  useEffect(() => {
    setEstadoListaPaises(data);

  }, []);
  return (
    <div className="divContenedorApp flexBoxColumn">
      <header>
        <h1> Paises</h1>
      </header>
      <main className="flexBoxColumn">
        <div className="divContenedorBuscar flexBoxColumn">
          <input
            spellCheck={false}
            value={estadoInput.buscar}
            onChange={(e) => {
              setEstadoInput({ buscar: e.currentTarget.value });
              getResult();
            }}
          />

          <div className="divContenedorOrdenarPor flexBoxRow">
            <h3>Ordenar por</h3>
            <button
              style={
                estadoBotones.SeleccionOrden == 0
                  ? { backgroundColor: "antiquewhite" }
                  : { backgroundColor: "" }
              }
              name="Continente"
              onClick={cambiarOrden}
            >
              Continente
            </button>
            <button
              style={
                estadoBotones.SeleccionOrden == 1
                  ? { backgroundColor: "antiquewhite" }
                  : { backgroundColor: "" }
              }
              name="Lenguaje"
              onClick={cambiarOrden}
            >
              Lenguaje
            </button>
          </div>
        </div>
        <div className="divContenedorArticulos flexBoxRowWrap">
          <ResultadoBusqueda />
        </div>
        <div className="divContenedorPaginacion flexBoxRow">
          <Paginacion
            hookEstadoPagina={estadoBotones}
            hookSetEstadoPagina={setEstadoBotones}
            listaPaises={data}
          />
        </div>
      </main>
      <footer></footer>
    </div>
  );
};
export default App;
