import React, { useEffect, useState } from "react";

const Paginacion = ({
  query,
  hookSetEstadoPagina,
  hookEstadoPagina,
  datos
}) => {
const [estadoCantidad, setEstadoCantidad] = useState(0);
  if (query[0]) return <p>Loading ...</p>;
  if (query[1]) return <p>error</p>;

let cantidadPaginas= [1];




if(datos){
  return cantidadPaginas.map((paginas) => (
    <button
      name={[paginas[0]]}
      style={
        hookEstadoPagina.EstadoPagina == paginas[0]
          ? { border: "solid .1rem red" }
          : { border: "" }
      }
      onClick={() => {
        hookSetEstadoPagina({
          ...hookEstadoPagina,
          EstadoPagina: paginas[0],
        });
      }}
    >
      {paginas[0] + 1}
    </button>
  ));
}


};

export default Paginacion;
