import React, { useEffect, useState } from "react";

const Paginacion = ({
  hookEstadoPagina,
  hookSetEstadoPagina,
  hookconfigurarArrayDatos,
  query,
  queryLazy,
  listaPaises
}) => {
  if (query[0]) return <p>Loading ...</p>;
  if (query[1]) return <p>error</p>;

/* if(listaPaises)
{
  
} */


  let cantidad = hookEstadoDataInfo
    ? query[2]?.countries.length
    : queryLazy[2]?.countries.length;

  /* listaPaises?.countries.length; */

  let cantidadPaginas = 0;
  let arrayPaginacion = [];

  if (cantidad % 10 == 0) {
    cantidadPaginas = cantidad / 10;
  } else {
    cantidadPaginas = cantidad / 10 + 1;
  }

  for (let i = 0; i < cantidadPaginas; i++) {
    arrayPaginacion.push([i]);
  }


  return arrayPaginacion.map((paginas) => (
    <button
      name={[paginas[0]]}
      style={
        hookEstadoPagina.EstadoPagina == paginas[0]
          ? { border: "solid .1rem red" }
          : { border: "" }
      }
      onClick={() => {
        hookSetEstadoPagina({
          ...hookEstadoPagina,
          EstadoPagina: paginas[0],
        });
      }}
    >
      {paginas[0] + 1}
    </button>
  ));
};

export default Paginacion;
