import React, { useEffect } from "react";

const Paginacion = ({
  listaPaises,
  hookEstadoPagina,
  hookSetEstadoPagina,
  funcionFiltrar,
  hookEstadoInput,
}) => {


  let cantidadPaises = listaPaises.filter((str) => {
    return str.name.toLowerCase().indexOf(input) !== -1;
  }).length;
 
  let cantidadPaginas = 0;
  let arrayPaginacion = [];
  if (cantidadPaises % 10 == 0) {
    cantidadPaginas = cantidadPaises / 10;
  } else {
    cantidadPaginas = cantidadPaises / 10 + 1;
  }

  for (let i = 0; i < cantidadPaginas; i++) {
    arrayPaginacion.push([i]);
  }
  console.log(arrayPaginacion);
  return arrayPaginacion.map((paginas) => (
    <button
      name={[paginas[0]]}
      style={
        hookEstadoPagina.EstadoPagina == paginas[0]
          ? { border: "solid .1rem red" }
          : { border: "" }
      }
      onClick={() => {
        hookSetEstadoPagina({
          ...hookEstadoPagina,
          EstadoPagina: paginas[0],
        });
      }}
    >
      {paginas[0] + 1}
    </button>
  ));
};

export default Paginacion;
