import React, { useEffect, useState } from "react";

const Paginacion = ({

  hookEstadoPagina,
  hookSetEstadoPagina,
  hookListaPaises
}) => {

console.log(hookListaPaises);

const[estadoCantidad, setestadoCantidad] = useState({
  cantidad:250
})


   /* listaPaises?.countries.length; */

 
  let cantidadPaginas = 0;
  let arrayPaginacion = [];


useEffect(()=>{setestadoCantidad({cantidad:10})},[hookListaPaises])


  if (estadoCantidad.cantidad % 10 == 0) {
    cantidadPaginas = estadoCantidad.cantidad / 10;
  } else {
    cantidadPaginas = estadoCantidad.cantidad / 10 + 1;
  }

  for (let i = 0; i < cantidadPaginas; i++) {
    arrayPaginacion.push([i]);
  }
  console.log(arrayPaginacion);
  return arrayPaginacion.map((paginas) => (
    <button
      name={[paginas[0]]}
      style={
        hookEstadoPagina.EstadoPagina == paginas[0]
          ? { border: "solid .1rem red" }
          : { border: "" }
      }
      onClick={() => {
        hookSetEstadoPagina({
          ...hookEstadoPagina,
          EstadoPagina: paginas[0],
        });
      }}
    >
      {paginas[0] + 1}
    </button>
  ));
};

export default Paginacion;
