import React, { useEffect, useState } from "react";
import "./App.css";
import "./constantes.css";
import ApolloClient from "apollo-boost";
import { ApolloProvider, useQuery } from "@apollo/react-hooks";
import graphqlQuery from "./graphql";

const App = () => {
  const [estadoInput, setEstadoInput] = useState({
    buscar: "",
  });
  const [estadoBotones, setEstadoBotones] = useState({
    SeleccionOrden: 0,
  });
  const cambiarOrden = (e) => {
    setEstadoBotones({
      SeleccionOrden: e.currentTarget.name == "Continente" ? 0 : 1,
    });
  };
  const ResultadoBusqueda = () => {
    const { loading, error, data } = useQuery(graphqlQuery().filtrarPorNombre);
    if (loading) return <p>Loading ...</p>;
    if (error) return <p>error</p>;

    let datadd = data.countries;


    datadd.sort((a, b) => {
const nombre1 = a[estadoBotones.SeleccionOrden == 0 ? 'continent.name' :'languages[0].name'];
const nombre2 = b[estadoBotones.SeleccionOrden == 0 ? 'continent.name' :'languages[0].name'];
if(nombre1 < nombre2){
  return -1;
}
if(nombre1 > nombre2){
  return 1;
}
return 0
    });






    return datadd.map((asd, index) => {
      if (
        asd.name.toLowerCase().indexOf(estadoInput.buscar.toLowerCase()) !== -1
      ) {
        return (
          <article key={index}>
            <div>
              <h2>{asd.name}</h2>
              <h2>asd.capital</h2>
            </div>
          </article>
        );
      }
    });
  };

  return (
    <div className="divContenedorApp flexBoxColumn">
      <header>
        <h1> Paises</h1>
      </header>
      <main className="flexBoxColumn">
        <div className="divContenedorBuscar flexBoxColumn">
          <input
            spellCheck={false}
            value={estadoInput.buscar}
            onChange={(e) => {
              setEstadoInput({ buscar: e.currentTarget.value });
            }}
          />

          <div className="divContenedorOrdenarPor flexBoxRow">
            <h3>Ordenar por</h3>
            <button 
            style={estadoBotones.SeleccionOrden == 0 ? {backgroundColor:"antiquewhite"} :{backgroundColor:""} }
            name="Continente" onClick={cambiarOrden}>
              Continente
            </button>
            <button 
             style={estadoBotones.SeleccionOrden == 1 ? {backgroundColor:"antiquewhite"} :{backgroundColor:""}  }
            name="Lenguaje" onClick={cambiarOrden}>
              Lenguaje
            </button>
          </div>
        </div>
        <div className="divContenedorArticulos flexBoxRowWrap">
          <ResultadoBusqueda />
        </div>
      </main>
      <footer></footer>
    </div>
  );
};
export default App;
