      if (asd.name.toLowerCase().indexOf(estadoInput.buscar.toLowerCase()) !== -1) {
        return <article>
          <div>
            <h2>{asd.name}</h2>
            <h2>asd.capital</h2>
          </div>
        </article>;
      }
    });
  };

  return (
    <div className="divContenedorApp flexBoxColumn">
      <header>
        <h1> Paises</h1>
      </header>
      <main className="flexBoxColumn">
        <div className="divContenedorBuscar flexBoxColumn">
          <input
            spellCheck={false}
            value={estadoInput.buscar}
            onChange={(e) => {
              setEstadoInput({ buscar: e.currentTarget.value });
            }}
          />

          <div className="divContenedorOrdenarPor flexBoxRow">
            <h3>Ordenar por</h3>
            <button name="Continente" onClick={cambiarOrden}>
              Continente
            </button>
            <button name="Lenguaje" onClick={cambiarOrden}>
              Lenguaje
            </button>
          </div>
        </div>
        <div className="divContenedorArticulos flexBoxRowWrap">
          <ResultadoBusqueda />
        </div>
      </main>
      <footer></footer>
    </div>
  );
};
export default App;
